import React, { Suspense,useEffect, useState} from 'react';
import {Route, Routes, useLocation} from "react-router-dom";
import {RoutesData} from "./RoutesData"
import HomeLayout from "../Helpers/HomeLayout";
const  AppRoutes = () => {
    const location = useLocation();
    const [routers , setRouters] = useState([])
    const {mains}  = RoutesData
    const scrollTop = () => {
        window.scrollTo({
            top: 0,
            left: 0,
            behavior : "smooth"
        });
    };
    useEffect(()=>{
        setRouters([...mains])
    })

    useEffect(() => {
        scrollTop();
    }, [location.pathname]);

    useEffect(() => {
        scrollTop();
    }, []);
    return (
        <Routes>
            {routers.map((route) => {
                let routeInner;
                let props = route;
                switch (route.layout) {
                    case "home":
                        routeInner = (
                            <HomeLayout {...props}>
                                <route.component />
                            </HomeLayout>
                        );
                        break;
                    case "about":
                        routeInner = (
                            <HomeLayout {...props}>
                                <route.component />
                            </HomeLayout>
                        );
                        break;

                    default:
                        break;
                }

                return (
                    <Route
                        path={route.url}
                        key={route.url}
                        element={
                            <Suspense>
                                {routeInner}
                            </Suspense>
                        }
                    />
                );
            })}
        </Routes>
    );
};

export default AppRoutes;
