import {CARDS, INTL, ITEM_PRODUCT} from "../Actions";
import {LOCALES} from "../../Intl/locales";
import {ReducerService} from "./ReducerService";

const initialState = {
    mainIntl : LOCALES.RUSSIAN,
    itemProduct : [],
    cards : []
}

export const StoreReducer = (state = initialState ,action ) => {
    switch (action.type) {
        case INTL :
            return {
                ...state,
                mainIntl: action.payload
            }
        case ITEM_PRODUCT :
            return {
                ...state,
                itemProduct: action.payload
            }
        case CARDS :
            return {
                ...state,
                cards: action.payload
            }
        default:
            return state
    }
}

export const reducerActions = {
    getCardAction: (data) => ({
        type: CARDS,
        payload: data
    })
}

export const createCards = () => async (dispatch) => {
    try {
        let response = await ReducerService.getCards();
        dispatch(reducerActions.getCardAction(response.data));
    } catch (e) {
        console.log(e.response.data)
    }
};
createCards()
